mapper.select:
SELECT t.*, m.mname, d.dname, dp.dpkind FROM telehealth t JOIN member m ON t.mno = m.mno JOIN doctor d ON t.dno = d.dno JOIN department dp ON t.dpno = dp.dpno WHERE t.dno = #{dno}
AND m.mname LIKE CONCAT('%', #{mname}, '%')
ORDER BY t.tdate DESC LIMIT 10;

SELECT COUNT(*) as count
FROM telehealth 
WHERE mno=#{mno} and hno=#{hno};
