mapper.update:
UPDATE review r
SET r.rlike = r.rlike + 1
WHERE r.rno = #{rno}

mapper.select:
SELECT r.rlike
FROM review r
WHERE r.rno = #{rno}

mapper.update:
UPDATE review r
SET r.rcontent = #{rcontent}
WHERE r.rno = #{rno}

mapper.select:
SELECT h.hno, h.dno, h.dspecialist, h.dpno, h.dpkind, h.dpkeyword
FROM hospitalList_view h
WHERE h.dno = #{dno}
